          <Form onSubmit={handleSubmit}>
            <InputGroup
              onChange={(e) => {
                handleChange(e);
              }}
              className="mb-5"
            >
              <FormControl
                value={state}
                placeholder="Add genre..."
                aria-label="Add genre"
                aria-describedby="basic-addon2"
              />
              <InputGroup.Append>
                <Button type="submit" variant="outline-secondary">
                  Button
                </Button>
              </InputGroup.Append>
            </InputGroup>
          </Form>
