import React, { useState, useEffect } from "react";
import {
  Card,
  ListGroup,
  InputGroup,
  FormControl,
  Button,
} from "react-bootstrap";
import genresData from "../../data/music/genres.json";
import { set } from "object-path";
export default function Index() {
//   const genres = genresData.genres;

const [genres, setGenres] = useState(['Rock','Pop','EDM',]);
const [state, setState] = useState(null);

  const listItems = genres.map((value) => (
    <ListGroup.Item>{value}</ListGroup.Item>
  ));

  const handleSubmit = () => {
    if(state != null){
      setGenres([...genres, state])
      setState(null)
    } 

  }
  const handleChange = (e) => {
    const inputGenre = e.target.value
    setState(inputGenre)
  }

  return (
    <div className="container">
      <h1>Genre list </h1>
      <InputGroup 
      onChange={(e) => {handleChange(e)}}

      style={{ width: "30rem" }} className="mb-3">
        <FormControl
          value={state}
          placeholder="Input genre"
          aria-label="Input genre"
          aria-describedby="basic-addon2"
        />
        <InputGroup.Append>
          <Button 
          onClick={() => {handleSubmit()}}
          variant="outline-secondary">Button</Button>
        </InputGroup.Append>
      </InputGroup>

      <Card style={{ width: "30rem" }}>
        <ListGroup variant="flush">{listItems}</ListGroup>
      </Card>
    </div>
  );
}
