import React, { useState, useEffect } from "react";
import {
  Card,
  ListGroup,
  InputGroup,
  FormControl,
  Button,
  Form,
  Pagination,
} from "react-bootstrap";
import genresData from "../../data/music/genres.json";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";
// import Pagination from "../../components/pagination/Pagination";

export default function Index() {
  //   const genres = genresData.genres;
  const MySwal = withReactContent(Swal);
  const [genres, setGenres] = useState([]);
  const [state, setState] = useState("");
const [currentPage, setCurrentPage] = useState(1);
const [postPerPage, setPostPerPage] = useState(10);

  useEffect(() => {
    setGenres(genresData.genres);
  }, []);

// Get current perPage 1 * 10 = 10
const indexOfLastPost = currentPage * postPerPage;
// 10 - 10 = 0
const indexOfFirstPost = indexOfLastPost - postPerPage;
// (0,10) จะนำตัวที่ 1 - 10 มาแสดงให้แสดงต่อหน้า
const currentPost = genres.slice(indexOfFirstPost, indexOfLastPost)


  const listItems = currentPost.map((value, index) => {
    return (
      <>
        <ListGroup.Item key={index}>
          {value}
          <a
            onClick={() => {
              handleDel(value);
            }}
            className="btn-sm float-right"
          >
            <i class="flaticon2-cancel text-danger"></i>
          </a>
        </ListGroup.Item>
      </>
    );
  });

  const handleSubmit = (event) => {
    event.preventDefault();
    if (state != "") setGenres([state, ...genres]);
    setState("");
  };
  const handleChange = (e) => {
    const inputGenre = e.target.value;
    setState(inputGenre);
  };
  const handleDel = (value) => {
    MySwal.fire({
      position: "top",
      icon: "question",
      title: <h1 className="display-5">Are you sure ?</h1>,
      text: "Are you sure you want to delete data ?",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Yes, edit it!",
    }).then((result) => {
      if (result.value) {
        setGenres(genres.filter((v) => v != value));
      }
    });
  };

  return (
    <>
      <Card className="container"> 
        <Card.Header>
          <h3>Genre list </h3>
          <span className="d-block text-muted pt-2 font-size-sm">
            Add row and group actions
          </span>
        </Card.Header>
        <Card.Body>
          <Form onSubmit={handleSubmit}>
            <InputGroup
              onChange={(e) => {
                handleChange(e);
              }}
              className="mb-5"
            >
              <FormControl
                value={state}
                placeholder="Add genre..."
                aria-label="Add genre"
                aria-describedby="basic-addon2"
              />
              <InputGroup.Append>
                <Button type="submit" variant="outline-secondary">
                  Button
                </Button>
              </InputGroup.Append>
            </InputGroup>
          </Form>

          {/* style={{ width: "30rem" }} */}
          <Card>
            {genres != "" ? (
              <ListGroup variant="flush">{listItems}</ListGroup>
            ) : (
              <h1 className="text-center mt-5 mb-5">no data .... </h1>
            )}
          </Card>
        </Card.Body>
        <Pagination
        postsPerPage={postsPerPage}
        totalPosts={genres.length}
        paginate={paginate}
      />
      </Card>
    </>
  );
}
