import React from 'react'
import {toAbsoluteUrl} from "../../_helpers";
import {NavLink} from "react-router-dom";


export default function Aside() {
    return (

        <div className="login-aside d-flex flex-row-auto bgi-size-cover bgi-no-repeat p-10 p-lg-10"
        style={{
          backgroundImage: `url(${toAbsoluteUrl("/media/bg/bg-4.jpg")})`
        }}>

          {/*begin::Aside*/}

            {/*begin: Aside Container*/}
            <div className="d-flex flex-row-fluid flex-column justify-content-between">
              {/* start:: Aside header */}
              <NavLink to="/" className="flex-column-auto mt-5">
                <img
                    alt="Logo"
                    className="max-h-70px"
                    src={toAbsoluteUrl("/media/logos/logo-letter-1.png")}
                />
              </NavLink>
              {/* end:: Aside header */}

              {/* start:: Aside content */}
              <div className="flex-column-fluid d-flex flex-column justify-content-center">
                <h3 className="font-size-h1 mb-5 text-white">
                  Welcome to Metronic!
                </h3>
                <p className="font-weight-lighter text-white opacity-80">
                  The ultimate Bootstrap & React 16 admin theme framework for next
                  generation web apps.
                </p>
              </div>
              {/* end:: Aside content */}

              {/* start:: Aside footer for desktop */}
              <div className="d-none flex-column-auto d-lg-flex justify-content-between mt-10">
                <div className="opacity-70 font-weight-bold	text-white">
                  &copy; 2020 Metronic
                </div>
                <div className="d-flex">
                  <NavLink to="/terms" className="text-white">
                    Privacy
                  </NavLink>
                  <NavLink to="/terms" className="text-white ml-10">
                    Legal
                  </NavLink>
                  <NavLink to="/terms" className="text-white ml-10">
                    Contact
                  </NavLink>
                </div>
              </div>
              {/* end:: Aside footer for desktop */}
            </div>
            {/*end: Aside Container*/}

          {/*end::Aside*/} 


      </div>

    )
}
