import React, { useState, useEffect } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import { Modal, Button } from "react-bootstrap";
import { useHistory, Redirect } from "react-router-dom";
import firebase from "../../config/config";
import Axios from "axios";
import { Success } from "../../components/modal/ModalSuccess";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function AddUser(props) {
  const history = useHistory()
  const MySwal = withReactContent(Swal);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [show, setShow] = useState(true);
  const handleClose = () => {
    setShow(false)
    setTimeout(() => {
      history.goBack()
    }, 100)
};
  const handleShow = () => setShow(true);


useEffect(() => {
  handleShow()
}, [props.show])

  const initialValues = {
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    passwordConfirmation: "",
    phone: "",
  };

  const enableLoading = () => {
    setLoading(true);
  };
  const disableLoading = () => {
    setLoading(false);
  };

  const getInputClasses = (fieldname) => {
    if (formik.touched[fieldname] && formik.errors[fieldname]) {
      return "is-invalid";
    }
    if (formik.touched[fieldname] && !formik.errors[fieldname]) {
      return "is-valid";
    }
    return "";
  };

  const handleedit = (value, { setStatus, setSubmitting }) => {
    const email = value.email;
    const password = value.password;
    const phone = value.phone;
    const firstName = value.firstName;
    const lastName = value.lastName;
    // Axios.post('http://localhost:5001/myspace-dev-1ae9e/us-central1/backoffice-user/newUser'
    // ,{email,password,phone,firstName,lastName})
    // .then((res) => {
    //   setStatus('Success !')
    //   formik.handleReset()
    // }).catch((err) => {
    //   var errorCode = err.code;
    //   var errorMessage = err.message;
    //   setStatus(errorCode)
    //   console.log(errorMessage)
    // })

    console.log(value);
    Success();
    disableLoading();
    setSubmitting(false);
    formik.handleReset();
    handleClose();
    //  MySwal.fire({
    //   position: 'center',
    //   icon: "success",
    //   title: <h1 className="display-3">success</h1>,
    //   showConfirmButton: false,
    // }).then(() => {
    //   disableLoading()
    //   setSubmitting(false);
    //   formik.handleReset()
    // })
  };

  const phoneRegExp = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/;

  const Userchema = Yup.object().shape({
    firstName: Yup.string()
      .min(3, "Minimum 3 symbols")
      .max(50, "Maximum 50 symbols")
      .required("Required"),
    lastName: Yup.string()
      .min(3, "Minimum 3 symbols")
      .max(50, "Maximum 50 symbols")
      .required("Required"),
    email: Yup.string()
      .email("Invalid email")
      .required("Required"),
    password: Yup.string()
      .oneOf([Yup.ref("passwordConfirmation"), null], "Passwords must match")
      .min(6, "Minimum 6 symbols")
      .required("Required"),
    passwordConfirmation: Yup.string()
      .oneOf([Yup.ref("password"), null], "Passwords must match")
      .min(6, "Minimum 6 symbols")
      .required("Required"),
    phone: Yup.string()
      .matches(phoneRegExp, "Phone number is not valid")
      .min(10, "min 10 symbols")
      .max(10, "Maximum 10 symbols")
      .required("Required"),
  });

  const formik = useFormik({
    initialValues,
    validationSchema: Userchema,
    onSubmit: (value, { setStatus, setSubmitting }) => {
      enableLoading();
      setTimeout(() => {
        handleedit(value, { setStatus, setSubmitting });
      }, 1000);
    },
  });

  return (
    <>
      <Modal
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        show={show}
        onHide={handleClose}
      >
        <Modal.Header >
          <Modal.Title>New User</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="row justify-content-center ">
            <div className="col-xl-12 col-xxl-12">
              {/*begin::Form Wizard*/}
              {formik.status ? (
                <div className="mb-10 alert alert-custom alert-light-danger alert-dismissible">
                  <div className="alert-text font-weight-bold">
                    {formik.status}
                  </div>
                </div>
              ) : null}
              <form onSubmit={formik.handleSubmit} className="form">
                {/*begin::Step 1*/}
                  <div className="row">
                    <div className="col-xl-12">
                      <div className="form-group row">
                        <label className="col-xl-3 col-lg-3 col-form-label">
                          Email Address
                        </label>
                        <div className="col-lg-9 col-xl-9">
                          <div className="input-group input-group-lg ">
                            <div className="input-group-prepend">
                              <span className="input-group-text">
                                <i className="la la-at" />
                              </span>
                            </div>
                            <input
                              name="email"
                              type="email"
                              className={`form-control form-control-lg  ${getInputClasses(
                                "email"
                              )}`}
                              name="email"
                              placeholder="Email"
                              {...formik.getFieldProps("email")}
                            />
                          </div>
                          {formik.touched.email && formik.errors.email ? (
                            <div className="fv-plugins-message-container">
                              <div className="fv-help-block">
                                {formik.errors.email}
                              </div>
                            </div>
                          ) : null}
                        </div>
                      </div>

                      <div className="form-group row">
                        <label className="col-xl-3 col-lg-3 col-form-label">
                          Password
                        </label>
                        <div className="col-lg-9 col-xl-9">
                          {/* <div className="input-group input-group-lg input-group-solid"> */}
                          <input
                            name="password"
                            type="password"
                            className={`form-control form-control-lg  ${getInputClasses(
                              "password"
                            )}`}
                            name="password"
                            placeholder="password"
                            {...formik.getFieldProps("password")}
                          />
                          {formik.touched.password && formik.errors.password ? (
                            <div className="fv-plugins-message-container">
                              <div className="fv-help-block">
                                {formik.errors.password}
                              </div>
                            </div>
                          ) : null}
                          {/* </div> */}
                        </div>
                      </div>

                      <div className="form-group row">
                        <label className="col-xl-3 col-lg-3 col-form-label">
                          passwordConfirmation
                        </label>
                        <div className="col-lg-9 col-xl-9">
                          {/* <div className="input-group input-group-lg input-group-solid"> */}
                          <input
                            name="passwordConfirmation"
                            type="password"
                            className={`form-control form-control-lg  ${getInputClasses(
                              "passwordConfirmation"
                            )}`}
                            name="passwordConfirmation"
                            placeholder="passwordConfirmation"
                            {...formik.getFieldProps("passwordConfirmation")}
                          />
                          {formik.touched.passwordConfirmation &&
                          formik.errors.passwordConfirmation ? (
                            <div className="fv-plugins-message-container">
                              <div className="fv-help-block">
                                {formik.errors.passwordConfirmation}
                              </div>
                            </div>
                          ) : null}
                          {/* </div> */}
                        </div>
                      </div>
                    </div>
                  </div>
                {/*end::Step 1*/}
              </form>
              {/*end::Form Wizard*/}
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button
            onClick={() => handleClose() }
            variant="secondary"
            className="btn btn-secondary font-weight-bold px-9 py-4 mr-3"
          >
            cancel
          </Button>

          <Button
            id="kt_login_signin_submit"
            type="submit"
            className={`btn btn-success font-weight-bold px-9 py-4 my-3`}
            disabled={formik.isSubmitting}
            variant="success"
            onClick={formik.handleSubmit}
          >
            submit
            {loading && <span className="ml-3 spinner spinner-white"></span>}
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
